import axios from "axios";
import React, { useState, useEffect } from "react";

const Comments_privacy = () => {
  const [home_data, sethomeData] = useState();
  const [homeLength, sethomeLength] = useState(0);

  //modal
  const [modalOpen, setModalOpen] = useState(false);
    const openModal = (e) => {
        setModalOpen(true);
    };
    const closeModal = () => {
        setModalOpen(false);
    };

  useEffect(() => {
    LoadData();
  }, []);

  const LoadData = async() => {
    axios.get('/mypage/', {
    })
    .then(function (response) {
      console.log("id: " + response.data.home_results.user_id)
      console.log("created_at: " + response.data.home_results.created_at)
      console.log("id_admin: " + response.data.home_results.id_admin)
      console.log("user_salt" + response.data.home_results.user_salt)
      console.log("user_hash: " + response.data.home_results.user_hashedPassword)
      sethomeData(response.data.home_results)
      sethomeLength(response.data.home_results.length)

    })
    .catch(function (error) {
      console.log("can't load server's data.")
    })
  }

    return (
        <>
          <div className="privacy_Div">
            <img className="privacy_img" src="/svg/fi-rr-user.svg"></img>
            <p className="privacy_text">회원정보</p>
            <div className="privacy_contents">
              <p className="privacy_contents_text">ID   {homeLength != 0 ? home_data.user_id : null}</p>
              <p className="privacy_contents_text">가입일시   {homeLength != 0 ? home_data.created_at : null}</p>
              <button className="privacy_changePWD"  onClick={e => openModal(e)}>비밀번호 변경</button>
            </div>
          </div>
        </>
    );
}

export default Comments_privacy;
