import axios from "axios";
import React, { useState } from "react";
import { useParams, useLocation, useNavigation } from "react-router";
import MenuBar from '../components/MenuBar'

const HospitalDetail = () => {
  const [writer_id, setwriter_id] = useState();
  const [hospital_id, sethospital_id] = useState();
  const [description, setdescription] = useState();

  const location = useLocation();
  console.log(location.state)
  const data = location.state.data;

  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지
    console.log(e.target[0].value);
    console.log(e.target[1].value);
    console.log(e.target[2].value);

    const writer_id = e.target[0].value;
    const hospital_id = e.target[1].value;
    const description = e.target[2].value;
    setwriter_id(writer_id);
    sethospital_id(hospital_id);
    setdescription(description);

    if (e.target[0].value != '') {
      //요청
      axios.post("/write", {
        writer_id: writer_id,
        hospital_id: hospital_id,
        description: description,
      })
      .then(function (response) {
        alert("작성완료!");
        console.log(response.data[0])
      })
      .catch(function (error) {
        alert("실패");
      })
  }
  else {
      alert("입력해주세요.");
  }

}

    return (
        <div className="App">
          <MenuBar></MenuBar>
          <div className="MainTopDiv">
            <span className="MainTopSpan">{data.name}</span>
          </div>

          <form onSubmit={AfterSubmit}>
            <textarea name="description" id="description" cols="30" rows="5" placeholder="한 번 작성한 리뷰는 삭제 및 수정이 불가합니다. (5글자 이상부터 등록 가능)"></textarea>
            <input type="hidden" name="writer_id" value="hyj3463@naver.com"></input>
            <input type="hidden" name="hospital_id" value={data._id}></input>
            <button id="submitBtn" type="submit">완료</button>
          </form>




        </div>
    );
}

export default HospitalDetail;
