import axios from "axios";
import React, { useState, useEffect} from "react";
import { useLocation, useNavigate } from "react-router-dom";
import MenuBar from '../../components/MenuBar'
import Footer from "../../components/Footer";

const BoardComments = (props) => {
  const [user_id, setUserId] = useState();
  const navigate = useNavigate();
  const posting_id = props.posting_id;
  const board_type = props.board_type;
  const path = "/community/" + board_type + "/comment-post";

  useEffect(() => {
      console.log(posting_id)
  }, []);

  const GetData = async (e) => {
    axios.get(path, {
    })
    .then(function (response) {
      setUserId(response.data.user_id);
      if (response.data.admin_id) {
        setAdminId(response.data.admin_id);
      }
      else {
        setAdminId(null);
      }
      setcomments(response.data.comments);
      setLength(response.data.comments.length);
      console.log(response.data.comments)
    })
    .catch(function (error) {
    })
  }

  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지
    console.log("0번: " + e.target[0].value); //내용
    console.log("2번: " + e.target[2].value); //병원
    axios.get('/checkUser', {
    })
    .then(function (response) {
      if( response.data.user_id_id !== null) {
        setUserId(response.data.user_id_id)
      }
    })
    .catch(function (error) {
    })
    const description = e.target[0].value;
    const writer = e.target[1].value;
    const posting_id = e.target[2].value;

    if (e.target[0].value != '') {
      axios.post(path, {
        writer: writer,
        posting_id: posting_id,
        description: description,
      })
      .then(function (response) {
        alert(response.data.text);
        console.log(response.data.comments)
        GetData();
      })
      .catch(function (error) {
        alert("실패");
      })
  }
  else {
      alert("내용을 입력해주세요.");
  }
}

    return (
        <>
          <MenuBar></MenuBar>
          <div className="cardDiv">
          <form onSubmit={AfterSubmit} className="commentsForm">
            <textarea name="description" id="description" cols="30" rows="5" placeholder="댓글 작성"></textarea>
            <input type="hidden" name="writer" value=""></input>
            <input type="hidden" name="posting_id" value=""></input>
            <div className="SubmitBtnDiv">
            <button id="submitBtn" type="submit">완료</button>
            </div>
          </form>
          </div>
          <Footer></Footer>
        </>
    );
}

export default BoardComments;
