import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom'
import {useNavigate, useLocation} from "react-router";
import Contents_none from "../../components/mypageComponents/contents_none";

const Reviews_data = () => {
  const [user_data, setuserData] = useState();
  const [userLength, setuserLength] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    LoadData();
  }, []);

  const LoadData = async() => {
    axios.get('/admin/', {
    })
    .then(function (response) {
      console.log("user_results: " + response.data.users)
      setuserData(response.data.users)
      setuserLength(response.data.users.length)

    })
    .catch(function (error) {
      console.log("can't load server's data.")
    })
  }

  const openDetail = (value, e) => {
    navigate('/admin/'+value, { state: {user_id: value}});
  }



    return (
        <>
            {
                userLength == 0 ? 
                <Contents_none text="가입된 회원이 없습니다."></Contents_none> 
                :
                <div className="review_Div">
                  <table className="mypageReports_table" border="1">
                    <th></th>
                    <th>내용</th>
                    <th>작성일자</th>
                    <th>승인여부</th>

                  {
                    user_data.map((data,index) => (
                      <tr>
                      <td key={index} className="td0">{index+1}</td>
                      <td key={index} className="td1" onClick={e => openDetail(data._id, e)}>{data.user_id}</td>
                      <td key={index} className="td2">{data.created_at}</td>
                      <td key={index} className="td3">{data.is_admin ? "관리자" : "회원"}</td>
                    </tr>
                    )) 
                  }
                  </table>
                </div>
            }
        </>
    );
}

export default Reviews_data;
