import axios from "axios";
import React, { useState, useEffect } from "react";
import Contents_none from "../../components/mypageComponents/contents_none";

const Reviews_data = () => {
  const [Reports_data, setReportsData] = useState();
  const [ReportsLength, setReportsLength] = useState(0);

  useEffect(() => {
    LoadData();
  }, []);

  const LoadData = async() => {
    axios.get('/admin/', {
    })
    .then(function (response) {
      //writer_id, hospital_id, _id, created_at
      setReportsData(response.data.reports)
      setReportsLength(response.data.reports.length)

    })
    .catch(function (error) {
      console.log(error)
    })
  }


    return (
        <>
            {
              ReportsLength == 0 ?
              <Contents_none text="병원 리스트가 없습니다."></Contents_none> 
              :
              <div className="review_Div">
                {
                  Reports_data.map((data, index) => (
                    <div className="review_comment_div">
                        <span key={index} className="comment_No">{index+1}</span>
                        <span key={index} className="comment_Desc">{data.hospital_id[0].name}</span>
                        <button key={index} className="REBTN">거절</button><button className="ACBTN">승인</button>
                        <span key={index} className="admin_report_data">{data.writer_id}</span>
                    </div>
                  ))
                }
                </div>
            }
        </>
    );
}

export default Reviews_data;
