import axios from "axios";
import React, { useState, useEffect } from "react";
import { useParams, useLocation, useNavigation } from "react-router";
import MenuBar from '../components/MenuBar'
import Footer from "../components/Footer";

const HospitalDetail = () => {
  const [reviews, setreviews] = useState();
  const [reviewsLength, setLength] = useState(0);
  const [user_id, setUserId] = useState();
  const [admin_id, setAdminId] = useState(null);

  const location = useLocation();
  console.log(location.state)
  const data = location.state.data;
  const path = "/reviews/" + data._id;

  useEffect(() => {
    GetData();
  }, []);

  const GetData = (e) => {
    axios.get(path, {
    })
    .then(function (response) {
      alert(response.data.text + "   " + response.data.user_id);
      setUserId(response.data.user_id);
      if (response.data.admin_id) {
        setAdminId(response.data.admin_id);
      }
      else {
        setAdminId(null);
      }
      setreviews(response.data.reviews);
      setLength(response.data.reviews.length);
      console.log(response.data.reviews)
    })
    .catch(function (error) {
      alert("회원 아이디 가져오기 요청 실패");
    })
  }

  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지
    const description = e.target[0].value;
    const writer_id = '';
    const hospital_id = e.target[2].value;
    axios.get('/checkUser', {
    })
    .then(function (response) {
      if( response.data.user_id_id !== null) {
        writer_id = response.data.user_id_id;
      }
    })

    if (e.target[0].value != '') {
      axios.post("/reviews/write", {
        writer_id: writer_id,
        hospital_id: hospital_id,
        description: description,
      })
      .then(function (response) {
        alert(response.data.text);
        console.log(response.data.reviews)
        GetData();
      })
      .catch(function (error) {
        alert("실패");
      })
  }
  else {
      alert("입력해주세요.");
  }
}

    return (
        <div className="App">
          <MenuBar></MenuBar>
          <div className="MainTopDiv">
            <span className="MainTopSpan">{data.name}</span>
          </div>

          <form onSubmit={AfterSubmit} className="commentsForm">
            <textarea name="description" id="description" cols="30" rows="5" placeholder="한 번 작성한 리뷰는 삭제 및 수정이 불가합니다. (5글자 이상부터 등록 가능)"></textarea>
            <input type="hidden" name="hospital_id" value={data._id}></input>
            <div className="SubmitBtnDiv">
            <button id="submitBtn" type="submit">완료</button>
            </div>
          </form>


          {
              reviewsLength === 0 ? 
                <p className="commentsNoneText">등록된 리뷰가 없습니다.</p>
              :
              reviews.map((data, id) => (
                  <div className={id % 2 == 0 ? 'commentsDiv'+ 0 : 'commentsDiv'+ 1}>
                      <p key={id} className="writer_id">{data.writer_id[0].user_id}</p>
                      <p key={id} className="description">{data.description}</p>
                      {admin_id !== null ?
                        <button>삭제</button>
                      :
                        null
                      }
                  </div>
              ))
          }

          <Footer></Footer>
        </div>
    );
}

export default HospitalDetail;
