import axios from "axios";
import React, { useState } from "react";
import { useParams, useLocation, useNavigation } from "react-router";
import MenuBar from '../components/MenuBar'

const Signup= () => {
  const [id, setId] = useState('');
  const [pwd, setPwd] = useState('');

  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지
    const input_id = e.target[0].value;
    const input_pwd = e.target[1].value;
    setId(input_id);
    setPwd(input_pwd);

    if (e.target[0].value != '') {
      //요청
      axios.post("/process/signup", {
        createId: id,
        createPassword: pwd,
      })
      .then(function (response) {
        alert("회원가입 완료!");
      })
      .catch(function (error) {
        alert("실패");
      })
  }
  else {
      alert("입력해주세요.");
  }
}

    return (
        <div className="App">
          <MenuBar></MenuBar>
          <div className="MainTopDiv">
            <span className="MainTopSpan">회원가입</span>
          </div>

          <form onSubmit={AfterSubmit} className="commentsForm">
            <input type="text" required name="createId" class="form-control" id="createId" placeholder="ID" minlength="5" maxlength="20"></input>
            <input type="password" required name="createPassword" class="form-control" id="createPassword" placeholder="Password" minlength="7"></input>
            <button id="submitBtn" type="submit">완료</button>
          </form>
        </div>
    );
}

export default Signup;
