import axios from "axios";
import React, { useState } from "react";
import { useParams, useLocation, useNavigation } from "react-router";
import MenuBar from '../components/MenuBar'

const HospitalDetail = () => {
  const [writer_id, setwriter_id] = useState();
  const [hospital_id, sethospital_id] = useState();
  const [description, setdescription] = useState();

  const location = useLocation();
  console.log(location.state)
  const data = location.state.data;

  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지
    console.log(e.target[0].value);
    console.log(e.target[1].value);
    console.log(e.target[2].value);

    const writer_id = e.target[0].value;
    const hospital_id = e.target[1].value;
    const description = e.target[2].value;
    setwriter_id(writer_id);
    sethospital_id(hospital_id);
    setdescription(description);

    if (e.target[0].value != '') {
      //요청
      axios.post("/comments/write", {
        writer_id: writer_id,
        hospital_id: hospital_id,
        description: description,
      })
      .then(function (response) {
        alert("작성완료!");
        console.log(response.data[0])
      })
      .catch(function (error) {
        alert("실패");
      })
  }
  else {
      alert("입력해주세요.");
  }
  router.post("/write", async(req, res) => {
    req.session.user = {
        id: req.body.writer_id,
        authorized: true
    };
    if (req.session.admin || req.session.user) {
        const banned_words = ["시발", "씨발", "씨팔", "ㅅㅂ", "썅", "등신", "병신", "븅신", "븅딱", "좆", "개새끼", "년", "애미", "애비",
            "간나", "쌍간나", "종간나", "개새", "개돼지", "씹", "걸레", "고자", "남창", "느개비", "느금마", "니미", "뒤져", "또라이", "똘추",
            "맘충", "머저리", "지랄", "엠창", "호모" ]

        for (index in banned_words) {
            if (req.body.description.includes(banned_words[index])) {
                return res.send(`<script>alert("금칙어를 포함한 댓글입니다."); history.go(-1);</script>`);
            }
        }

        let comment = new Comments();
        comment.writer_id = req.body.writer_id;
        comment.hospital_id = req.body.hospital_id;
        comment.description = req.body.description;

        comment = await comment.save();
    }
    res.send({results: '성공!'});
});
}

    return (
        <div className="App">
          <MenuBar></MenuBar>
          <div className="MainTopDiv">
            <span className="MainTopSpan">{data.name}</span>
          </div>

          <form onSubmit={AfterSubmit} className="commentsForm">
            <textarea name="description" id="description" cols="30" rows="5" placeholder="한 번 작성한 리뷰는 삭제 및 수정이 불가합니다. (5글자 이상부터 등록 가능)"></textarea>
            <input type="hidden" name="writer_id" value="hyj3463@naver.com"></input>
            <input type="hidden" name="hospital_id" value={data._id}></input>
            <button id="submitBtn" type="submit">완료</button>
          </form>





        </div>
    );
}

export default HospitalDetail;
