import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import './modal.css';

const Post = (props) => {
  const { open, close, header } = props;
  const setModalOpen = props.setModalOpen;
  const navigate = useNavigate();


  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지

    if (e.target[0].value != '') {
      axios.post("/process/report", {
      })
      .then(function (response) {
        alert(response.data.text);
        setModalOpen(false);
      })
      .catch(function (error) {
        alert("신고 실패");
      })
  }
  else {
      alert("입력해주세요.");
  }
}

const ReportsSubmit = (e) => {
  if (e.target[0].value != '') {
    axios.post("/process/report", {
    })
    .then(function (response) {
      alert(response.data.text);
      setModalOpen(false);
    })
    .catch(function (error) {
      alert("신고 실패");
    })
}
else {
    alert("입력해주세요.");
}
}

  return (
    <div className={open ? 'openModal modal' : 'modal'}>
      {open ? (
        <section>
          <header>
            {header}
            <button className="close" onClick={close}>&times;</button>
          </header>
          <main>
          <form className="commentsForm" onSubmit={ReportsSubmit}>
            <p>현재 해당 병원에 문제가 발생하여 <strong>신고</strong>합니다.</p>
            <p>무분별한 신고는 제재 사유가 될 수 있으며, 신고 횟수 적용은 관리자의 승인 후 결정됩니다.</p>
            <p>아래 <strong>[동의 및 신고]</strong>를 클릭하시면 위 내용을 인지하고 <strong>동의</strong>하신 것으로 간주됩니다.</p>
            <button className="close" onClick={close}>취소</button>
            <button className="ReportsBtn" type="submit">동의 및 신고</button>
          </form>
          </main>
        </section>
      ) : null}
    </div>
  );
};

export default Post;
