import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import axios from "axios";
import './modal.css';

const Post = (props) => {
  const { open, close, header } = props;
  const setModalOpen = props.setModalOpen;
  const navigate = useNavigate();


  const AfterSubmit = (e) => {
    e.preventDefault(); //redirect 방지

    if (e.target[0].value != '') {
      axios.post("/process/report", {
      })
      .then(function (response) {
        alert(response.data.text);
        setModalOpen(false);
      })
      .catch(function (error) {
        alert("신고 실패");
      })
  }
  else {
      alert("입력해주세요.");
  }
}

  return (
    <div className={open ? 'openModal modal' : 'modal'}>
      {open ? (
        <section>
          <header>
            {header}
            <button className="close" onClick={close}>&times;</button>
          </header>
          <main>
          <form className="commentsForm" onSubmit={AfterSubmit}>
            <p>주의</p>
            <p>고객님께서는 현재 해당 병원에 문제가 발생하여 신고합니다.</p>

            <button className="close" onClick={close}>취소</button>
            <button className="ReportsBtn" type="submit">동의 및 신고</button>
          </form>
          </main>
        </section>
      ) : null}
    </div>
  );
};

export default Post;
