import React, {Component, useState} from "react";
import axios from 'axios';
import MenuBar from "../components/MenuBar";

class App extends Component {
    state = {
        text: "",
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value,
        });
    };

    onclick = () => {
        const textbox = {
            inText: this.state.text,
        };
        fetch("http://127.0.0.1:3001/text", {
            method: "post",
            headers: {
                "content-type": "application/json",
            },
            body: JSON.stringify(textbox),
        })
        .then((res) => res.json())
        .then((json) => {
            console.log(json);
            this.setState({
                text: json.text,
            });
        });
    };

    render() {
        return (
            <div>
                <input name="text" onChange={this.handleChange}></input>
                <button onClick={this.onclick}>전송</button>
                <h3>{this.state.text}</h3>

                <form action="/api/testapi/res" method="POST">
                    <div>
                        <select name="symptom_level">
                            <option selected>증상 정도</option>
                            <option value="1">매우 아픔</option>
                            <option value="2">조금 아픔</option>
                            <option value="3">보통</option>
                            <option value="4">약한 증상</option>
                            <option value="5">의심 증상</option>
                        </select>
                        <select name="symptom" >
                            <option selected>증상 선택</option>
                            <option>머리가 아파요</option>
                            <option>눈이 이상해요</option>
                            <option>뼈가 부러졌어요</option>
                            <option>코가 막혀요</option>
                            <option>이가 아파요</option>
                        </select>
                    </div>
                    <div>
                        <button type="submit">검색</button>
                    </div>
                </form>
            </div>
        );
    }
}

export default App;
